export function accessKeyModal(e){fetch(chrome.runtime.getURL("enter-key-modal.html")).then((e=>e.text())).then((e=>{const t=document.createElement("div");t.innerHTML=e,document.body.appendChild(t);const c=document.getElementById("accessKeyModal"),n=document.getElementById("accessKeyClose"),l=document.getElementById("confirmAccessKey"),s=document.getElementById("accessKeyInput");n.addEventListener("click",(()=>{c.style.display="none"})),c.style.display="block",l.addEventListener("click",(()=>{const e=s.value.trim();""!==e?(localStorage.setItem("accessKey",e),c.style.display="none",alert("Access key saved successfully")):alert("Please enter your access key.")}))})).catch((e=>{alert("Error loading modal"),console.log("Error loading modal",e)}))}